import React, { useState } from 'react';

const Sidebar = ({ categories, setSelectedCategory }) => {
  const [selected, setSelected] = useState('All'); // State to track selected category

  const handleClick = (category) => {
    setSelected(category); // Update selected category state
    setSelectedCategory(category); // Propagate category change to parent component
  };

  return (
    <div className="sidebar">
      <h2>Categories</h2>
      <ul>
        {categories.map((category, index) => (
          <li key={index} className={selected === category ? 'active' : ''} onClick={() => handleClick(category)}>
            {category}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
