import React, { useState } from 'react';
import './BottomCardProduct.css'

const ProductSlider = ({ products }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const numVisibleCards = 4; // Number of cards visible at a time

  const nextSlide = () => {
    const newIndex = currentIndex + 1;
    if (newIndex <= products.length - numVisibleCards) {
      setCurrentIndex(newIndex);
    }
  };

  const prevSlide = () => {
    const newIndex = currentIndex - 1;
    if (newIndex >= 0) {
      setCurrentIndex(newIndex);
    }
  };

  const disablePrev = currentIndex === 0;
  const disableNext = currentIndex >= products.length - numVisibleCards;

  return (
    <div className="product-slider-container">
      <h2>Featured Products</h2>
      <div className="product-slider">
        <div className="slider-wrapper" style={{ transform: `translateX(-${currentIndex * (100 / numVisibleCards)}%)` }}>
          {products.map((product, index) => (
            <div key={index} className="slide">
              <div className="bottom-product-card">
                <div className="card-image">
                  <img src={product.imageSrc} alt={product.name} />
                </div>
                <div className="card-content">
                  <h3>{product.name}</h3>
                  <p className="about">{product.about}</p>
                  <p className="price">{`$${product.price}`}</p>
                  <button>Add to Cart</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        {!disablePrev && (
          <button className="slider-btn prev" onClick={prevSlide}>
            &#8249;
          </button>
        )}
        {!disableNext && (
          <button className="slider-btn next" onClick={nextSlide}>
            &#8250;
          </button>
        )}
      </div>
    </div>
  );
};

export default ProductSlider;
