import React, { useState } from 'react';
import './Product.css';
import Sidebar from './Sidebar';
import ProductCard from './ProductCard';
import HeaderImage from './HeaderImage';

const categories = ['All', 'Dog', 'Cat', 'Rabbit', 'Rodents', 'Fish', 'Bird', 'Accessories', 'Pet Food', 'Aquarium'];

const products = [
  { id: 1, category: 'Dog', name: 'Dog Food', imageSrc: 'dog-food.jpg', price: '$20' },
  { id: 1, category: 'Dog', name: 'Dog Food', imageSrc: 'dog-food.jpg', price: '$20' },
  { id: 1, category: 'Dog', name: 'Dog Food', imageSrc: 'dog-food.jpg', price: '$20' },
  { id: 1, category: 'Dog', name: 'Dog Food', imageSrc: 'dog-food.jpg', price: '$20' },
  { id: 1, category: 'Dog', name: 'Dog Food', imageSrc: 'dog-food.jpg', price: '$20' },
  { id: 1, category: 'Dog', name: 'Dog Food', imageSrc: 'dog-food.jpg', price: '$20' },
  { id: 2, category: 'Cat', name: 'Cat Toys', imageSrc: 'cat-toys.jpg', price: '$15' },
  { id: 2, category: 'Cat', name: 'Cat Toys', imageSrc: 'cat-toys.jpg', price: '$15' },
  { id: 2, category: 'Cat', name: 'Cat Toys', imageSrc: 'cat-toys.jpg', price: '$15' },
  { id: 2, category: 'Cat', name: 'Cat Toys', imageSrc: 'cat-toys.jpg', price: '$15' },
  { id: 3, category: 'Rabbit', name: 'Rabbit Cage', imageSrc: 'rabbit-cage.jpg', price: '$30' },
  { id: 4, category: 'Fish', name: 'Fish Tank', imageSrc: 'fish-tank.jpg', price: '$50' },
  { id: 4, category: 'Fish', name: 'Fish Tank', imageSrc: 'fish-tank.jpg', price: '$50' },
  { id: 4, category: 'Fish', name: 'Fish Tank', imageSrc: 'fish-tank.jpg', price: '$50' },
  { id: 4, category: 'Fish', name: 'Fish Tank', imageSrc: 'fish-tank.jpg', price: '$50' },
  { id: 5, category: 'Bird', name: 'Bird Feeder', imageSrc: 'bird-feeder.jpg', price: '$10' },
  // Add more products as needed
];

function ProductList() {
  const [selectedCategory, setSelectedCategory] = useState('All');
//   const [filteredProducts, setFilteredProducts] = useState(products);
const [filteredProducts, setFilteredProducts] = useState(() => {
    return selectedCategory === 'All' ? products : products.filter(product => product.category === selectedCategory);
  });

  const filterProducts = (category) => {
    if (category === 'All') {
      setFilteredProducts(products);
    } else {
      const filtered = products.filter(product => product.category === category);
      setFilteredProducts(filtered);
    }
  };

  const handleCategoryChange = (category) => {
    // selectedCategory(category);
    setSelectedCategory(category);
    // filterProducts(category);
  };

  return (
    <div className="App">
      <HeaderImage />

      <div className="container">
        <Sidebar categories={categories} setSelectedCategory={handleCategoryChange} />

        <div className="product-grid">
          {filteredProducts.length === 0 ? (
            <p>No products found in this category</p>
          ) : (
            filteredProducts.map(product => (
              <ProductCard key={product.id} name={product.name} imageSrc={product.imageSrc} price={product.price} />
            ))
          )}
        </div>

        <div className="pagination">
          <p>More items available...</p>
        </div>
      </div>
    </div>
  );
}

export default ProductList;
