import React, { useState } from 'react';
import './styles.css';

const categories = ['All', 'Dog', 'Cat', 'Accessories', 'Rabbit', 'Rodents', 'Fish', 'Bird', 'Pet Food', 'Aquarium'];
const products = [
  { id: 1, name: 'Dog Food', category: 'Dog', image: 'dog_food.jpg', description: 'Nutritious dog food for all breeds.', price: '$20' },
  { id: 2, name: 'Cat Toy', category: 'Cat', image: 'cat_toy.jpg', description: 'Fun and engaging cat toy.', price: '$10' },
  { id: 3, name: 'Dog Collar', category: 'Accessories', image: 'dog_collar.jpg', description: 'Stylish and durable dog collar.', price: '$15' },
  { id: 4, name: 'Cat Bed', category: 'Cat', image: 'cat_bed.jpg', description: 'Comfortable bed for cats.', price: '$25' },
  // Add more products as needed
];

const ProductList = () => {
  const [selectedCategory, setSelectedCategory] = useState('All');

  const selectCategory = (category) => {
    setSelectedCategory(category);
  };

  const filteredProducts = selectedCategory === 'All'
    ? products
    : products.filter(product => product.category === selectedCategory);

  return (
    <div className="App">
      <div className="sidebar">
        <ul>
          {categories.map(category => (
            <li key={category} onClick={() => selectCategory(category)}>
              {category}
            </li>
          ))}
        </ul>
      </div>
      <div className="main-content">
        <div className="product-grid">
          {filteredProducts.map(product => (
            <div className="product-card" key={product.id}>
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <div className="price">{product.price}</div>
              <button className="add-to-cart">Add to Cart</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductList;
