import React, { useState } from 'react';
import './BottomCardProduct.css';

const ProductSlider = ({ products }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const numVisibleCards = 4; // Number of cards visible at once

  const nextSlide = () => {
    const lastIndex = products.length - numVisibleCards;
    const index = currentIndex + 1;
    setCurrentIndex(Math.min(index, lastIndex));
  };

  const prevSlide = () => {
    const index = currentIndex - 1;
    setCurrentIndex(Math.max(index, 0));
  };

  const sliderWrapperStyle = {
    transform: `translateX(-${currentIndex * (100 / numVisibleCards)}%)`
  };

  return (
    <div className="product-slider-container">
      <h2>Featured Products</h2>
      <div className="product-slider">
        <div className="slider-wrapper" style={sliderWrapperStyle}>
          {products.map((product, index) => (
            <div key={index} className="slide">
              <img src={product.imageSrc} alt={product.name} />
              <h3>{product.name}</h3>
              <p className="about">{product.about}</p>
              <p>{`$${product.price}`}</p>
              <button>Add to Cart</button>
            </div>
          ))}
        </div>
        {currentIndex > 0 && (
          <button className="slider-btn prev" onClick={prevSlide}>
            &#8249;
          </button>
        )}
        {currentIndex < products.length - numVisibleCards && (
          <button className="slider-btn next" onClick={nextSlide}>
            &#8250;
          </button>
        )}
      </div>
    </div>
  );
};

export default ProductSlider;
